{
    "name": "service name", //Optional parameter to name this service 
    "owner_ref": "xzy", //Free data field that will be returned as-is in response to an event. It can be used as means to co-relate requests and responses.
    "auto_record": false, //Recording by default or on command	
    "from": "CLI number", //The CLI assigned on the outbound number. This number is matched with the configured number.
    "to": "Destination number", //Destination number
    "action_on_connect": //If set, prompt will be played automatically when destination number is connected
    {
        "room": {
        "room_id" : "room_id"
        }
    },
        "connect": {
            "from": "CLI",
            "to": "bridge number"
        },

    "play": {
        "prompt_name": "prompt_name", //Plays the prompt that has been uploaded with Prompt Id through portal provisioning
                        "asr": true
    },          //OR
    "play": {
        "text": "your prompt here", //TTS to play the prompt according to the parameter defined as following parameters below
        "language": "en-US", //The preferred spoken language for TTS 
        "voice": "female", //Voice gender
        "asr": true //Enable Automatic Speech Recognition, to detect words/sentences and return in the text
    },
    "start_timeout": 10, //Initial silence timeout before user start speaking. Range : 1 – 10 seconds. Default : 10 seconds.
    "recognizer_timeout": 1, //Wait before the voice server returns the result after silence. Range : 1 – 10 seconds. Default : 2 seconds.              

"event_url": "https://yourserver.com/webhook" //Webhook URL to be called for the application to take control
}

{
	"from": "CLI number",         //Originating phone number – also known as Caller Id
	"to" : "Destination number"       //The destination number or SIP URI
}

{
	"start": true,            //Boolean. Required to start recording; set to true.
	"recording_name": "my_voice_recording"        //If User specifies recording_name and any recording file with same name already exist, the request will fail 
}

{
"hold": true        //true: to hold the bridged call leg. false: to resume the bridged call leg.
}

{
	"stop": true        //Boolean. Required to stop recording; set to true.
}
